function [code] = Sound2bin(fs, chemin)[y, Fs] = audioread(chemin);audiowrite('reduced_'+chemin, y, fs);[y, Fs] = audioread('reduced_'+chemin);code = [];for i = 1:1:size(y)    numstr = num2str(y(i));    splt = split(numstr, '.');    if size(splt, 1) > 1        temp = char(splt(2));        bin = dec2bin(str2double(temp));            else        temp = char(splt(1));        bin = dec2bin(str2double(temp));            code = [code bin];        end%sound(y);T = 1 / Fs;L = size(y);t = (0:L-1) * T;plot(t, y);end